import React from 'react'
import styles from './login.module.css'
import loginImage from '../../assets/images/login-page-image.jpg'
import InputControl from '../common/inputControl/InputControl'
import { useState } from 'react'
import { createUserWithEmailAndPasswrd } from 'firebase/auth'
import { auth } from '../../firebase'

export default function Login() {

    const [data, setData] = useState({
        email: "",
        password: '',
    })

    const [error, setError] = useState("")

    const handleSubmit = (e) => {
        e.preventDefault()
        if (!data.email || data.password) {
            setError("This field is required")
        }
        else {
            setError("")
        }
        createUserWithEmailAndPasswrd(auth, data.email, data.password).the((res) => {
            console.log(res)
        }).catch((err) => {
            console.log(err)
        })
    }


    return (
        <div className={styles.login}>
            <div className={styles.loginModal}>
                <div className={styles.imageDiv}>
                    <img src={loginImage} alt="" />
                </div>
                <form action="">
                    <h4 className={styles.loginHeading}>Login</h4>
                    <InputControl
                        placeholder="Enter your Email"
                        onChange={(e) => { setData((prev) => ({ ...prev, email: e.target.value })) }}
                    />
                    <div className={styles.errorMsg}> {error}</div>
                    <InputControl
                        placeholder="Enter your password"
                        onChange={(e) => { setData((prev) => ({ ...prev, password: e.target.value })) }}
                    />
                    <div className={styles.errorMsg}> {error}</div>
                    <button type='submit' onClick={handleSubmit}>Submit</button>
                </form>
            </div>
        </div >
    )
}
